function display_alert(message){
  $(".currentalert").remove();
  $("body").append('<div class="currentalert"> <i class="fas fa-bell"></i> <br>'+ message +' <button>&times;</button></div>');
  $(".currentalert button").click(function(){
    $(".currentalert").remove();
  });
}
jQuery(document).ready(function($){
  $("[data-alert]").click(function(){
    var message = $(this).data("alert");
    display_alert(message);
  });
});


jQuery(document).ready(function($){
  $(".revealsidebar").click(function(){
    $(".sidebar").fadeIn();
    $(".hidesidebar").fadeIn();
    $(this).fadeOut();
  });
  $(".hidesidebar").click(function(){
    $(".sidebar").fadeOut();
    $(".revealsidebar").fadeIn();
    $(this).fadeOut();
  });
});
